function toggleSection(section){const loginButton=document.querySelector('#login-button');const registerButton=document.querySelector('#register-button');const heading=document.querySelector('.login-register__heading');const registerForm=document.querySelector('.register__form');const loginForm=document.querySelector('.login__form');if(section=='login'){loginButton.classList.add('active');registerButton.classList.remove('active');heading.textContent='Login';loginForm.style.display='block';registerForm.style.display='none';} else if(section=='register'){registerButton.classList.add('active');loginButton.classList.remove('active');heading.textContent='Register';registerForm.style.display='block';loginForm.style.display='none';}}